pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol"; // Import Ownable contract
import "./ICompliancePropertyManager.sol";

contract MockSwap is Ownable { // Inherit the Ownable contract
    IERC20 public usdtToken;
    uint256 public conversionRate; // Conversion rate in USDT per 1 native token (e.g., 1 ETH = 3000 USDT)
    ICompliancePropertyManager public propertyManager;
    bytes32 public ruleKey;

    event SwapUSDTForNative(address indexed user, uint256 usdtAmount, uint256 nativeAmount);
    event SwapNativeForUSDT(address indexed user, uint256 nativeAmount, uint256 usdtAmount);

    constructor(IERC20 _usdtToken, uint256 _conversionRate, ICompliancePropertyManager _propertyManager, bytes32 _ruleKey) {
        usdtToken = _usdtToken;
        conversionRate = _conversionRate;
        propertyManager = _propertyManager;
        ruleKey = _ruleKey;
    }

    function swapUSDTForNative(uint256 usdtAmount, uint256 mid) external {
        require(propertyManager.verified(mid, ruleKey), "Rule verification failed");
        require(usdtAmount > 0, "Amount must be greater than 0");
        uint256 nativeAmount = usdtAmount / conversionRate;
        require(nativeAmount > 0, "Not enough USDT for conversion");

        // Transfer USDT from user to contract
        usdtToken.transferFrom(msg.sender, address(this), usdtAmount);

        // Transfer native tokens from contract to user
        (bool success, ) = msg.sender.call{value: nativeAmount}("");
        require(success, "Native token transfer failed");

        emit SwapUSDTForNative(msg.sender, usdtAmount, nativeAmount);
    }

    function swapNativeForUSDT(uint256 mid) external payable {
        require(propertyManager.verified(mid, ruleKey), "Rule verification failed");
        uint256 nativeAmount = msg.value;
        require(nativeAmount > 0, "Amount must be greater than 0");
        uint256 usdtAmount = nativeAmount * conversionRate;

        // Transfer USDT from contract to user
        usdtToken.transfer(msg.sender, usdtAmount);

        emit SwapNativeForUSDT(msg.sender, nativeAmount, usdtAmount);
    }

    // Fallback function to receive native tokens (ETH)
    receive() external payable {}

    // Function to withdraw accidentally sent tokens
    function withdrawAccidentalTokens(IERC20 _token) external onlyOwner { // Add the onlyOwner modifier
        require(address(_token) != address(usdtToken), "Cannot withdraw USDT");

        uint256 balance = _token.balanceOf(address(this));
        _token.transfer(msg.sender, balance);
    }

    // Function to withdraw accidentally sent native tokens (ETH)
    function withdrawAccidentalNativeTokens() external onlyOwner { // Add the onlyOwner modifier
        uint256 balance = address(this).balance;
        (bool success, ) = msg.sender.call{value: balance}("");
        require(success, "Native token transfer failed");
    }

    function setPropertyManager(ICompliancePropertyManager _propertyManager) external onlyOwner { // Add the onlyOwner modifier
        propertyManager = _propertyManager;
    }
}
